// sample size configs not documented. Found here: https://github.com/bheisler/criterion.rs/issues/407
criterion_group! {
    name = ol_benches;
    config = Criterion::default().sample_size(10);  // sampling size
    targets = bench_delay
}

criterion_main!(ol_benches);

